import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Container,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Grid,
  IconButton,
  Tooltip,
} from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const categories = [
  {
    id: 1,
    name: 'Herbal Face Packs',
    products: [
      { id: 101, name: 'Neem Face Pack', price: 250, image: 'https://via.placeholder.com/150' },
      { id: 102, name: 'Aloe Vera Pack', price: 300, image: 'https://via.placeholder.com/150' },
      { id: 103, name: 'Multani Mitti Pack', price: 200, image: 'https://via.placeholder.com/150' },
    ],
  },
  {
    id: 2,
    name: 'Herbal Detox Teas',
    products: [
      { id: 201, name: 'Green Tea', price: 150, image: 'https://via.placeholder.com/150' },
      { id: 202, name: 'Tulsi Tea', price: 180, image: 'https://via.placeholder.com/150' },
      { id: 203, name: 'Ginger Tea', price: 160, image: 'https://via.placeholder.com/150' },
    ],
  },
];

function HomePage() {
  const logout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handleAddToCart = (product) => {
    alert(`${product.name} added to cart! (Demo)`);
  };

  return (
    <>
      <Container sx={{ mt: 5, mb: 8 }}>
        {categories.map((category) => (
          <Box key={category.id} sx={{ mb: 6 }}>
            <Typography
              variant="h5"
              sx={{
                mb: 3,
                borderBottom: '3px solid #388e3c',
                display: 'inline-block',
                paddingBottom: '4px',
                fontWeight: 'bold',
              }}
            >
              {category.name}
            </Typography>

            <Box
              sx={{
                display: 'flex',
                overflowX: 'auto',
                gap: 3,
                pb: 1,
                '&::-webkit-scrollbar': {
                  height: 6,
                },
                '&::-webkit-scrollbar-thumb': {
                  backgroundColor: '#388e3c',
                  borderRadius: 3,
                },
              }}
            >
              {category.products.map((product) => (
                <Card
                  key={product.id}
                  sx={{
                    minWidth: 220,
                    flexShrink: 0,
                    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                    transition: 'transform 0.3s ease',
                    '&:hover': {
                      transform: 'scale(1.05)',
                      boxShadow: '0 6px 20px rgba(0,0,0,0.15)',
                    },
                    cursor: 'pointer',
                  }}
                  onClick={() => alert(`Clicked on ${product.name}`)}
                >
                  <CardMedia
                    component="img"
                    height="160"
                    image={product.image}
                    alt={product.name}
                    sx={{ objectFit: 'cover' }}
                  />
                  <CardContent>
                    <Typography variant="subtitle1" component="div" fontWeight="600">
                      {product.name}
                    </Typography>
                    <Typography variant="h6" color="#388e3c" fontWeight="bold" mt={1}>
                      ₹{product.price}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      variant="contained"
                      size="small"
                      sx={{ backgroundColor: '#388e3c', '&:hover': { backgroundColor: '#2e7d32' } }}
                      onClick={(e) => {
                        e.stopPropagation(); // prevent card onClick
                        handleAddToCart(product);
                      }}
                    >
                      Add to Cart
                    </Button>
                  </CardActions>
                </Card>
              ))}
            </Box>
          </Box>
        ))}
      </Container>

      <Box
        component="footer"
        sx={{
          py: 3,
          textAlign: 'center',
          borderTop: '1px solid #ccc',
          backgroundColor: '#f4f6f8',
          mt: 'auto',
        }}
      >
        <Typography variant="body2" color="text.secondary">
          © 2025 HerbalShop. All rights reserved.
        </Typography>
      </Box>
    </>
  );
}

export default HomePage;
