// src/pages/HomePage.jsx
import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, Button } from '@mui/material';

const dummyData = [
  {
    id: 1,
    name: 'Electronics',
    products: [
      { id: 101, name: 'Mobile Phone', price: 15000, image: 'https://via.placeholder.com/150' },
      { id: 102, name: 'Laptop', price: 55000, image: 'https://via.placeholder.com/150' },
    ],
  },
  {
    id: 2,
    name: 'Clothing',
    products: [
      { id: 201, name: 'T-Shirt', price: 500, image: 'https://via.placeholder.com/150' },
      { id: 202, name: 'Jeans', price: 1200, image: 'https://via.placeholder.com/150' },
    ],
  },
];

const HomePage = () => {
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Backend call can be added here later
    setCategories(dummyData);
  }, []);

  const handleAddToCart = (product) => {
    setCart(prevCart => {
      // Check if product already in cart
      const exists = prevCart.find(item => item.id === product.id);
      if (exists) {
        // Increase quantity
        return prevCart.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
    alert(`${product.name} added to cart!`);
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Product Categories
      </Typography>

      {categories.map(category => (
        <div key={category.id} style={{ marginBottom: '40px' }}>
          <Typography variant="h5" sx={{ mb: 2 }}>
            {category.name}
          </Typography>

          <Grid container spacing={3}>
            {category.products.map(product => (
              <Grid item xs={12} sm={6} md={3} key={product.id}>
                <Card>
                  <CardMedia
                    component="img"
                    height="140"
                    image={product.image}
                    alt={product.name}
                  />
                  <CardContent>
                    <Typography variant="h6">{product.name}</Typography>
                    <Typography color="text.secondary">â‚¹{product.price}</Typography>
                    <Button
                      variant="contained"
                      color="primary"
                      sx={{ mt: 1 }}
                      fullWidth
                      onClick={() => handleAddToCart(product)}
                    >
                      Add to Cart
                    </Button>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}

      <Typography variant="h5" sx={{ mt: 6 }}>
        Cart Items: {cart.length}
      </Typography>
      {cart.map(item => (
        <Typography key={item.id}>
          {item.name} x {item.quantity}
        </Typography>
      ))}
    </Container>
  );
};

export default HomePage;
